sys_variable_value.value:
(function execute(inputs, outputs) {
// Convert Received Email to Attachment
var record = new GlideRecord(inputs.table_name);
record.get(inputs.record_sysid);

var strEmailContent = inputs.content_type + "\n" + inputs.headers + "\n\n\n\n" + inputs.body_text + "  " + "\n";

var attach = new GlideSysAttachment();
attach.write(record, inputs.subject + ".eml","application/octet-stream", strEmailContent);


})(inputs, outputs);

