sys_variable_value.value:
(function execute(inputs, outputs) {
    var kbQuery = inputs.kb_query || "active=true^published=true";
    var maxArticles = parseInt(inputs.max_articles || 100, 10);
    var timeout = parseInt(inputs.timeout_seconds || 5, 10);

    var brokenLinks = [];
    var urlRegex = /(https?:\/\/[^\s"']+)/g;

    var kb = new GlideRecord('kb_knowledge');
    kb.addEncodedQuery(kbQuery);
    kb.setLimit(maxArticles);
    kb.query();

    while (kb.next()) {
        var content = kb.getValue('text') || "";
        var urls = content.match(urlRegex);
        if (!urls) continue;

        for (var i = 0; i < urls.length; i++) {
            var url = urls[i];

            // Skip internal links (if needed)
            if (url.indexOf(gs.getProperty('instance_name')) > -1) continue;

            try {
                var r = new sn_ws.RESTMessageV2();
                r.setHttpMethod("HEAD");
                r.setEndpoint(url);
                r.setRequestHeader("User-Agent", "ServiceNow-LinkChecker");
                r.setRequestHeader("Accept", "*/*");
                r.setHttpTimeout(timeout * 1000);

                var response = r.execute();
                var status = response.getStatusCode();

                if (status >= 400) {
                    brokenLinks.push({
                        article_sys_id: kb.getUniqueValue(),
                        article_number: kb.getValue('number'),
                        article_title: kb.getValue('short_description'),
                        bad_url: url,
                        http_status: status
                    });
                }
            } catch (ex) {
                brokenLinks.push({
                    article_sys_id: kb.getUniqueValue(),
                    article_number: kb.getValue('number'),
                    article_title: kb.getValue('short_description'),
                    bad_url: url,
                    http_status: "Error: " + ex.message
                });
            }
        }
    }

    outputs.broken_links = brokenLinks;
    outputs.count_broken = brokenLinks.length;

})(inputs, outputs);

