sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var count=0;
var tb=new GlideRecord(inputs.tablename);
tb.addEncodedQuery(inputs.encodedquery);
tb.query();
while(tb.next()){
    tb.setValue(inputs.fieldname,inputs.fieldvalue);
    tb.update();
    count++;
}
outputs.updated_count=count;
})(inputs, outputs);

