sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // Change 1: Input type should be 'Reference' instead of 'String'
        var userSysId = inputs.userSysId;

        var userGR = new GlideRecord('sys_user');
        if (!userGR.get(userSysId)) {
            throw new Error("User not found for Sys ID: " + userSysId);
        }

        var managerchain = [];
        var currentUser = userGR;

        while (currentUser.manager) {
            // Change 2: Prevent infinite loop if user is their own manager
            if (currentUser.manager.toString() === currentUser.sys_id.toString()) {
                throw new Error("User is their own manager. Stopping loop to prevent infinite recursion.");
            }

            var mgrGR = new GlideRecord('sys_user');
            if (!mgrGR.get(currentUser.manager)) {
                throw new Error("Manager record not found for Sys ID: " + currentUser.manager);
            }

            managerchain.push({
                name: mgrGR.name.toString(),
                email: mgrGR.email.toString(),
                title: mgrGR.title.toString(),
                sys_id: mgrGR.sys_id.toString()
            });

            currentUser = mgrGR;
        }

        outputs.managerchain = managerchain;
        outputs.error = "";
    } catch (ex) {
        gs.error("Error in GetUserManagerChain: " + ex.message);
        outputs.managerchain = [];
        outputs.error = ex.message;
    }
})(inputs, outputs);

