sys_variable_value.value:
(function execute(inputs, outputs) {

    var restMessage = new sn_ws.RESTMessageV2();
restMessage.setEndpoint(inputs.endpoint);
restMessage.setHttpMethod('GET');
var username = inputs.user;
var password = inputs.pwd;
restMessage.setBasicAuth(username, password);
restMessage.setMIDServer(inputs.mid_server_name);  // Replace with actual MID Server name
var response = restMessage.execute();
if (response.haveError()) {
  //  gs.info('Error: ' + response.getErrorMessage());
    outputs.rest_result_response =  response.getErrorMessage();
} else {
    var httpStatus = response.getStatusCode();
    var responseBody = response.getBody();
    outputs.rest_result_status =  httpStatus;
    outputs.rest_result_response =  responseBody;
    //gs.info(httpStatus + ' ' + responseBody);
}



})(inputs, outputs);

