sys_variable_value.value:
/**
 * Check if DateTime is Weekend
 * 
 * Determines if the provided DateTime falls on a weekend (Saturday or Sunday).
 * Returns true if weekend, false otherwise. Handles null/undefined inputs by returning false
 * and logging a warning. Designed for use in a scoped app Flow Action.
 * 
 * APIs Used:
 * - GlideDateTime: For date manipulation and day-of-week extraction.
 * - gs.error: For logging errors in scoped environments.
 * 
 */
(function execute(inputs, outputs) {
    
    // Initialize output to false as default (handles invalid cases)
    outputs.weekend = false;
    
    try {
        // Create GlideDateTime from valid input
        var gdt = new GlideDateTime(inputs.InputDatetime);
        
        // Get day of week (1=Monday, 7=Sunday; local time)
        var dayOfWeek = gdt.getDayOfWeekLocalTime();
        
        // Set output: true for Saturday (6) or Sunday (7)
        outputs.weekend = (dayOfWeek == 6 || dayOfWeek == 7);
        
    } catch (e) {
        // Catch any unexpected GlideDateTime errors (e.g., malformed date string)
        gs.error('Weekend Check Flow Action: Error processing InputDatetime - ' + e.message + '. Defaulting to non-weekend.');
       throw new Error("Error: " + error.message);
    }
})(inputs, outputs);

