sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var arr=[];
var k= inputs.parameter;
var gr= new GlideRecord("task");
gr.addActiveQuery();
gr.addQuery("assignment_group",k);
gr.query();
while(gr.next()){
    arr.push({number: gr.number.toString(),
            sys_id: gr.sys_id.toString()});
}
outputs.tickets=arr;
})(inputs, outputs);

