sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var k = inputs.usrr;
var arr=[];
var str;
var gr= new GlideRecord("task");//parent table for all tables
gr.addQuery("assigned_to",k);
gr.addActiveQuery();//active query
gr.query();
while(gr.next()){
	arr.push(gr.number);
}
if(arr.length>0){
	str="Please find the tickets "+arr.join(",")+" that are active and assigned to :"+k; 
}
else{
	str="There are no active tickets, please proceed with the deactivation of the user's profile";
}
outputs.str=str;
})(inputs, outputs);

