sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        //var variables = inputs.requested_item.variables.getElements();
        var result = {};
        var pairs = inputs.variable_template.split('^');
        gs.info('Service Catalog Debug: Variable Template: '+inputs.variable_template);

        pairs.forEach(pair => {
            // Find the index of the first '=' character
            var equalIndex = pair.indexOf('=');

            // If '=' is found and it's not the first or last character
            if (equalIndex > 0 && equalIndex < pair.length - 1) {
                // Partition the key and value
                var key = pair.substring(0, equalIndex).trim();
                var value = pair.substring(equalIndex + 1).trim();

                // Store it in the result object
                result[key] = value;
            }
        });

        var cart = new sn_sc.CartJS();
            var item = {
                'sysparm_id': inputs.catalog_item,
                'sysparm_quantity': '1',
                'variables': result
            };
            gs.info('Service Catalog Debug: '+JSON.stringify(item));
            
            cart.setRequestedFor(inputs.requested_for);
            cart.addToCart(item);
            var checkoutInfo = cart.checkoutCart();
            if (!gs.nil(checkoutInfo)) {
                outputs.success = true;
                outputs.message = 'Successfully submitted request '+checkoutInfo.request_number.toString();
                var requestGR = new GlideRecord('sc_request');
                if (requestGR.get(checkoutInfo.request_id.toString())){
                    outputs.request = requestGR;
                }                
            } else {
                outputs.success = false;
                outputs.message = 'Failed to submit request';
            }
    } catch (e) {
        outputs.success = false;
        outputs.message = 'There is an error while submitting the request. Error:' + e;
    }

})(inputs, outputs);

