sys_variable_value.value:
/**
 * CI Health Checker Action
 * 
 * Analyzes Configuration Item (CI) health by:
 * - Checking mandatory fields completeness
 * - Validating relationships (upstream/downstream)
 * - Identifying orphaned CIs (no relationships)
 * - Calculating overall data quality score (0-100)
 * - Providing actionable recommendations
 * 
 * @param ci_sys_id (String, Mandatory) - The sys_id of the CI to analyze
 * @param check_relationships (Boolean, Optional, Default=true) - Whether to check CI relationships
 * 
 * @output health_score (Integer) - Overall health score from 0-100
 * @output missing_fields (String) - Comma-separated list of missing mandatory fields
 * @output relationship_count (Integer) - Total number of relationships
 * @output is_orphaned (Boolean) - True if CI has no relationships
 * @output recommendations (String) - Actionable recommendations for improvement
 * @output error (String) - Error message if any
 */

(function execute(inputs, outputs) {
    try {
        // Initialize outputs
        outputs.health_score = 0;
        outputs.missing_fields = '';
        outputs.relationship_count = 0;
        outputs.is_orphaned = false;
        outputs.recommendations = '';
        outputs.error = '';
        
        // Validate input
        if (!inputs.ci_sys_id || inputs.ci_sys_id === '') {
            outputs.error = 'CI sys_id is required';
            outputs.health_score = 0;
            return;
        }
        
        // Get the CI record
        var ciGR = new GlideRecord('cmdb_ci');
        if (!ciGR.get(inputs.ci_sys_id)) {
            outputs.error = 'CI not found with sys_id: ' + inputs.ci_sys_id;
            outputs.health_score = 0;
            return;
        }
        
        var score = 100; // Start with perfect score
        var missingFieldsList = [];
        var recommendationsList = [];
        
        // 1. Check mandatory fields (40 points)
        var mandatoryFields = ['name', 'operational_status', 'install_status', 'support_group'];
        var missingCount = 0;
        
        for (var i = 0; i < mandatoryFields.length; i++) {
            var field = mandatoryFields[i];
            if (!ciGR.getValue(field) || ciGR.getValue(field) === '') {
                missingFieldsList.push(field);
                missingCount++;
            }
        }
        
        if (missingCount > 0) {
            var fieldPenalty = (missingCount / mandatoryFields.length) * 40;
            score -= fieldPenalty;
            recommendationsList.push('Fill in missing mandatory fields: ' + missingFieldsList.join(', '));
        }
        
        outputs.missing_fields = missingFieldsList.join(', ');
        
        // 2. Check relationships (30 points)
        var checkRels = inputs.check_relationships !== undefined ? inputs.check_relationships : true;
        
        if (checkRels) {
            // Count parent relationships
            var parentRels = new GlideRecord('cmdb_rel_ci');
            parentRels.addQuery('child', inputs.ci_sys_id);
            parentRels.query();
            var parentCount = parentRels.getRowCount();
            
            // Count child relationships
            var childRels = new GlideRecord('cmdb_rel_ci');
            childRels.addQuery('parent', inputs.ci_sys_id);
            childRels.query();
            var childCount = childRels.getRowCount();
            
            outputs.relationship_count = parentCount + childCount;
            
            if (outputs.relationship_count === 0) {
                outputs.is_orphaned = true;
                score -= 30;
                recommendationsList.push('CI is orphaned - establish relationships with other CIs');
            } else if (outputs.relationship_count < 2) {
                score -= 15;
                recommendationsList.push('CI has limited relationships - consider adding more connections');
            }
        }
        
        // 3. Check CI class specificity (15 points)
        var ciClass = ciGR.getValue('sys_class_name');
        if (ciClass === 'cmdb_ci') {
            score -= 15;
            recommendationsList.push('CI uses generic class - consider using a more specific CI class');
        }
        
        // 4. Check last discovered date (15 points)
        var lastDiscovered = ciGR.getValue('last_discovered');
        if (!lastDiscovered || lastDiscovered === '') {
            score -= 10;
            recommendationsList.push('CI has never been discovered - consider setting up discovery');
        } else {
            var lastDiscoveredGDT = new GlideDateTime(lastDiscovered);
            var now = new GlideDateTime();
            
            // Get milliseconds difference and convert to days
            var lastDiscoveredMS = lastDiscoveredGDT.getNumericValue();
            var nowMS = now.getNumericValue();
            var diffMS = nowMS - lastDiscoveredMS;
            var daysOld = Math.floor(diffMS / (1000 * 60 * 60 * 24));
            
            if (daysOld > 90) {
                score -= 15;
                recommendationsList.push('CI data is stale (last discovered ' + daysOld + ' days ago) - run discovery');
            } else if (daysOld > 30) {
                score -= 5;
                recommendationsList.push('CI data is aging (last discovered ' + daysOld + ' days ago)');
            }
        }
        
        // Ensure score is within bounds
        outputs.health_score = Math.max(0, Math.min(100, Math.round(score)));
        
        // Build recommendations
        if (recommendationsList.length === 0) {
            outputs.recommendations = 'CI health is excellent! No immediate actions required.';
        } else {
            outputs.recommendations = recommendationsList.join('; ');
        }
        
        // Add health status summary
        var healthStatus = '';
        if (outputs.health_score >= 90) {
            healthStatus = 'Excellent';
        } else if (outputs.health_score >= 70) {
            healthStatus = 'Good';
        } else if (outputs.health_score >= 50) {
            healthStatus = 'Fair';
        } else {
            healthStatus = 'Poor';
        }
        
        gs.info('CI Health Check completed for ' + ciGR.getValue('name') + ' - Score: ' + outputs.health_score + ' (' + healthStatus + ')');
        
    } catch (ex) {
        outputs.error = 'Error analyzing CI health: ' + ex.message;
        outputs.health_score = 0;
        gs.error('CI Health Checker Error: ' + ex.message);
    }
})(inputs, outputs);

