sys_variable_value.value:
(function execute(inputs, outputs) {
    // Default values
    outputs.is_birthday = false;
    outputs.message = "";

    // Get user record and check whether they have birth date
    var user = inputs.user;
    if (!user || !user.u_birth_date) {
        outputs.message = "Birth date not found";
        return;
    }

    // Get today's date and user's birth date (MM-DD format)
    var today = new GlideDateTime().getLocalDate().toString().substring(5,10);
    var dob = user.u_birth_date.substring(5,10);

    // Compare month and day
    if (today === dob) {
        outputs.is_birthday = true;
        outputs.message = "Today is " + user.user_name + "'s birthday!";
    } else {
        outputs.message = "Today isn't " + user.user_name + "'s birthday!";
    }
})(inputs, outputs);

