sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var usr=inputs.parameter;
var str;
var arr=[]; //empty array to capture all the reportees
var gr= new GlideRecord("sys_user");
gr.addQuery("manager",usr);
gr.query();
while(gr.next()){//finding all the users whose manager is same as the selected person's manager
arr.push(gr.sys_id);//storing all the sysid's into the array
}
if(arr.length>0){
str=arr.join(",");
}
else{
    str= "";
}
outputs.usrid=str;

})(inputs, outputs);

