sys_variable_value.value:
(function execute(inputs, outputs) {
    var latestJournalEntry = "";
    var record = new GlideRecord(inputs.tablename);
    if (record.get(inputs.sysid)) {
        if (inputs.journaltype == 'work_notes')
            latestJournalEntry = record.work_notes.getJournalEntry(1);
        else
            latestJournalEntry = record.comments.getJournalEntry(1);
    }
    outputs.comment = latestJournalEntry;
})(inputs, outputs);

