sys_variable_value.value:
(function execute(inputs, outputs) {
 var table = inputs.tableName;      // e.g., 'incident'
  var recId = inputs.recordSysId;    // sys_id of record

  if (!table || !recId) {
    gs.warn('Missing required inputs: tableName or recordSysId');
    outputs.summaryText = '';
    return;
  }

  // Create GlideJournalEntry object for the record
  var gr = new GlideRecord(table);
  gr.get(recId);
  var journal= gr.work_notes.getJournalEntry(-1);
  //var journal = new GlideJournalEntry(table, recId);

  // Get all work notes entries (returns an array of objects with 'value', 'createdBy', 'createdOn')
  //var entries = journal.getJournalEntry('work_notes'); // only work_notes
  var entries=journal;
  var lines = [];
  lines.push('# Work Notes Summary\n');

  if (entries && entries.length > 0) {
    for (var i = 0; i < entries.length; i++) {
      var entry = entries[i];
      var created = entry.createdOn.getDisplayValue();
      var author = entry.createdBy.getDisplayValue();
      var note = entry.value + '';

      lines.push('**' + created + ' — ' + author + '**');
      lines.push('');
      lines.push(note);
      lines.push('\n---\n');
    }
  } else {
    lines.push('_No work notes found._');
  }

  var summary = lines.join('\n');

  // Post as a comment
  try {
    var gr = new GlideRecord(table);
    if (gr.get(recId)) {
      gr.comments = summary; // posts summary to comments
      gr.update();
      gs.info('Posted work notes summary as comment on ' + table + ':' + recId);
    } else {
      gs.warn('Record not found for ' + table + ' / ' + recId);
    }
  } catch (e) {
    gs.error('Error posting work notes summary: ' + e.message);
  }

  // Set output for Flow Designer
  outputs.summaryText = summary;
})(inputs, outputs);

