sys_variable_value.value:
(function execute(inputs, outputs) {
var table = inputs.tableName;
  var recId = inputs.recordSysId;
  var maxLen = 32000;

  if (!table || !recId) {
    gs.warn('Missing required inputs: tableName or recordSysId');
    outputs.summarytext = '';
    return;
  }

  var journal = new GlideRecord('sys_journal_field');
  journal.addQuery('name', table);
  journal.addQuery('element_id', recId);
  journal.addQuery('element', 'work_notes');
  journal.orderBy('sys_created_on');
  journal.query();

  var lines = [];
  lines.push('# Work Notes Summary\n');

  var count = 0;
  while (journal.next()) {
    count++;
    var created = journal.sys_created_on.getDisplayValue();
    var author = journal.sys_created_by.getDisplayValue();
    var note = journal.value + '';

    // Format as markdown
    lines.push('**' + created + ' — ' + author + '**');
    lines.push('');
    lines.push(note);
    lines.push('\n---\n');
  }

  if (count === 0) {
    lines.push('_No work notes found._');
  }

  var summary = lines.join('\n');

  // Truncate if needed to fit comment field safely
  if (summary.length > maxLen) {
    summary = summary.substring(0, maxLen - 100) + '\n\n*(Summary truncated)*';
  }

  outputs.summarytext = summary;

  try {
    var gr = new GlideRecord(table);
    if (gr.get(recId)) {
      gr.comments = summary; // post as comment (visible to requester)
      gr.update();
      gs.info('Posted work notes summary as comment on ' + table + ':' + recId);
    } else {
      gs.warn('Record not found for ' + table + ' / ' + recId);
    }
  } catch (e) {
    gs.error('Error posting work notes summary: ' + e.message);
  }
})(inputs, outputs);

