sys_variable_value.value:
(function execute(inputs, outputs) {
    try {

        // Check if the tag exists
        var tagGR = new GlideRecord('label');
        tagGR.addQuery('name', inputs.tagName);
        tagGR.query();

        var tagSysId;
        var tagCreated = 'false';

        if (tagGR.next()) {
            tagSysId = tagGR.sys_id;
        } else {
            // Create the tag
            tagGR.initialize();
            tagGR.name = inputs.tagName;
            tagGR.setValue('viewable_by','everyone');
            tagSysId = tagGR.insert();
            tagCreated = 'true';
        }

        // Validate the record exists
        var recordGR = new GlideRecord(inputs.tableName);
        if (!recordGR.get(inputs.recordSysId)) {
            outputs.taggingSuccess = false;
            throw new Error('Record not found in table: ' + inputs.tableName);
        }

        // Add the tag to the record
        var labelEntryGR = new GlideRecord('label_entry');
        labelEntryGR.initialize();
        labelEntryGR.title=inputs.tagName;
        labelEntryGR.label = tagSysId;
        labelEntryGR.table = inputs.tableName;
        labelEntryGR.table_key = inputs.recordSysId.sys_id;
        labelEntryGR.insert();

        // Set outputs only on success.
        outputs.tagsysid = tagSysId;
        outputs.tagcreated = tagCreated;
        outputs.taggingsuccess = 'true';
        outputs.errormessage = '';

    } catch (e) {
        // Handle unexpected errors
        outputs.taggingsuccess = 'false';
        outputs.errormessage = 'Unexpected error: ' + e.message;
        throw new Error('Unexpected error: ' + e.message);
        

        // Do NOT set tagSysId or tagCreated in error case
    }
})(inputs, outputs);

