sys_variable_value.value:
(function execute(inputs, outputs) {
    try {

        var calendarName = inputs.calendar_name;
        var inputDateTime = new GlideDateTime(inputs.input_datetime);
        var checkType = inputs.check_type; // 'Business Hours', 'Holiday', 'Maintenance Window'
        var timezone = inputs.timezone || gs.getSession().getTimeZoneName();

        // Output defaults
        var isValid=false;
        outputs.is_valid = isValid ? "true" : "false";
        outputs.message = "Validation failed.";
        outputs.next_valid_time = "";

        // Get the Business Calendar
        var calendarGR = new GlideRecord('cmn_schedule');
        calendarGR.addQuery('name', calendarName);
        calendarGR.query();

        if (!calendarGR.next())
            throw new Error("Business Calendar not found: " + calendarName);

        var schedule = new GlideSchedule(calendarGR.sys_id);
        schedule.setTimeZone(timezone);

        switch (checkType) {
            case 'Business Hours':
                isValid=schedule.isInSchedule(inputDateTime);
                outputs.is_valid = isValid ? "true" : "false";
                outputs.message = isValid ? "Within business hours." : "Outside business hours.";

                // Manual workaround for next valid time
                if (!isValid) {
                    var nextValid = findNextValidTime(schedule, inputDateTime, 48); // Look ahead 48 hours
                    if (nextValid) {
                        outputs.next_valid_time = nextValid;
                    }
                }
                break;

            case 'Holiday':
                var holidayGR = new GlideRecord('cmn_schedule_span');
                holidayGR.addQuery('schedule', calendarGR.sys_id);
                holidayGR.addQuery('type', 'holiday');
                holidayGR.addQuery('start_date', '<=', inputDateTime);
                holidayGR.addQuery('end_date', '>=', inputDateTime);
                holidayGR.query();
                isValid=!holidayGR.hasNext();
                outputs.is_valid = isValid ? "true" : "false";
                outputs.message = isValid ? "Not a holiday." : "Holiday detected.";
                break;

            case 'Maintenance Window':
                var mwGR = new GlideRecord('cmn_schedule_span');
                mwGR.addQuery('schedule', calendarGR.sys_id);
                mwGR.addQuery('type', 'maintenance');
                mwGR.addQuery('start_date', '<=', inputDateTime);
                mwGR.addQuery('end_date', '>=', inputDateTime);
                mwGR.query();
                isValid=mwGR.hasNext();
                outputs.is_valid = isValid ? "true" : "false";
                outputs.message = isValid ? "Within maintenance window." : "Outside maintenance window.";
                break;

            default:
                throw new Error("Invalid check_type: " + checkType);
        }

    } catch (e) {
        outputs.is_valid = 'false';
        outputs.message = "Error: " + e.message;
        outputs.next_valid_time = "";
    }

    // Helper function to simulate nextStartTime
    function findNextValidTime(schedule, startTime, maxHoursAhead) {
        var checkTime = new GlideDateTime(startTime);
        var hoursChecked = 0;

        while (hoursChecked < maxHoursAhead) {
            checkTime.addSeconds(3600); // Check every hour
            if (schedule.isInSchedule(checkTime)) {
                return checkTime.getDisplayValue();
            }
            hoursChecked++;
        }

        return ""; // No valid time found within range
    }

})(inputs, outputs);

