sys_variable_value.value:
(function execute(inputs, outputs) {
    const recCount = inputs.count;
    outputs.count = recCount;
    outputs.records = inputs.records;

    if(recCount == 0){
        outputs.message = `Scope ${inputs.scope_id} does not have any version records available.`;
        outputs.scope_installed = false;
    } else {
        inputs.records.next();
        const installed = gs.nil(inputs.records.getValue('store_application')) ? false : true;
        outputs.scope_installed = installed
        outputs.message = `Scope ${inputs.scope_id} has ${recCount} versions available, and is${installed ? '' : ' not'} installed.`
    }
})(inputs, outputs);

