sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var managerSysId = '';
        var managerName = '';

        // Load the user record
        var userGR = new GlideRecord('sys_user');
        if (userGR.get(inputs.UserSysId)) {
            if (userGR.manager) {  // Check if manager is set
                var mgrGR = new GlideRecord('sys_user');
                if (mgrGR.get(userGR.manager)) {
                    managerSysId = mgrGR.sys_id.toString();
                    managerName = mgrGR.name.toString();
                }
            }
        } else {
            gs.error('User not found: ' + inputs.UserSysId);
        }

        // Set outputs
        outputs.manager_sys_id = managerSysId;
        outputs.manager_name = managerName;

    } catch (e) {
        gs.error('Error fetching manager: ' + e.message);
        outputs.manager_sys_id = '';
        outputs.manager_name = '';
    }
})(inputs, outputs);

