sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var str = inputs.inputString; // Input as email or Phone Number
        var op = inputs.stringOperation;  
        var regexPatternString = inputs.regex;  // Example input regex for phone number ^(\+91[\-\s]?)?[0]?(91)?[6789]\d{9}$ // Example regex for email as ^([a-zA-Z0-9_.-]+)@([a-zA-Z0-9.-]+)\.([a-zA-Z]{2,})$
        const regexPattern = new RegExp(regexPatternString);

        switch (op) {
            case "email":
                outputs.result = validateEmail(str,regexPattern);
                break;
            case "phone_number":
                outputs.result = validatePhone(str, regexPattern);
                break;
            default:
                outputs.result = 'Invalid option passed';
        }
        outputs.success = true;
        outputs.message = "Operation executed successfully.";

    } catch (e) {
        outputs.success = false;
        outputs.message = "Error: " + e.message;
    }

    function validateEmail(email, regexpattern1) {
        return regexpattern1.test(email);     
    }

    function validatePhone(phone, regexpattern2) {
        return regexpattern2.test(phone);      
    }

})(inputs, outputs);

