sys_variable_value.value:
(function() {
    try {
        // ============================================
        // INPUT VALIDATION & NORMALIZATION
        // ============================================
        
        gs.addInfoMessage("1");

        // Normalize email to lowercase
        var normalizedEmail = inputs.email.trim().toLowerCase();
        
        // Basic email format validation
        var emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(normalizedEmail)) {
            outputs.isvalid = false;
            outputs.email = normalizedEmail;
            outputs.domain = '';
            outputs.errormessage = 'Email format is invalid';
            outputs.message = 'Validation failed: Email does not match required format';
            return;
        }
        
        // ============================================
        // EXTRACT AND VALIDATE EMAIL DOMAIN
        // ============================================
        
        var emailParts = normalizedEmail.split('@');
        var extractedDomain = emailParts[1];
        
        // ============================================
        // PARSE ALLOWED DOMAINS
        // ============================================
        
        // Validate allowed_domains input exists
        if (!inputs.allowed_domains || typeof inputs.allowed_domains !== 'string') {
            outputs.isvalid = false;
            outputs.email = normalizedEmail;
            outputs.domain = extractedDomain;
            outputs.errormessage = 'Allowed domains list is required and must be a valid string';
            outputs.message = 'Validation failed: No allowed domains configured';
            return;
        }
        
        // Parse and normalize allowed domains
        var allowedDomainsList = inputs.allowed_domains
            .split(',')
            .map(function(domainItem) {
                return domainItem.trim().toLowerCase();
            })
            .filter(function(domainItem) {
                return domainItem.length > 0;
            });
        
        // Validate we have at least one allowed domain
        if (allowedDomainsList.length === 0) {
            outputs.isvalid = false;
            outputs.email = normalizedEmail;
            outputs.domain = extractedDomain;
            outputs.errormessage = 'No valid allowed domains provided';
            outputs.message = 'Validation failed: Allowed domains list is empty';
            return;
        }
        
        // ============================================
        // PERFORM DOMAIN VALIDATION
        // ============================================
        
        var isDomainAllowed = allowedDomainsList.indexOf(extractedDomain) !== -1;
        
        // ============================================
        // SET OUTPUT VALUES
        // ============================================
        
        outputs.isvalid = isDomainAllowed;
        outputs.email = normalizedEmail;
        outputs.domain = extractedDomain;
        outputs.alloweddomainscount = allowedDomainsList.length;
        
        if (isDomainAllowed) {
            outputs.message = 'Email domain is approved: ' + extractedDomain;
            outputs.statuscode = 'APPROVED';
        } else {
            outputs.message = 'Email domain is not in the allowed list: ' + extractedDomain;
            outputs.statuscode = 'REJECTED';
        }
        
    } catch (error) {
        // ============================================
        // ERROR HANDLING
        // ============================================
        
        gs.error('Email Validator Flow Action Error: ' + error.message);
        
        outputs.isvalid = false;
        outputs.email = inputs.email || '';
        outputs.domain = '';
        outputs.errormessage = 'An unexpected error occurred during validation';
        outputs.message = 'Validation failed: ' + error.message;
        outputs.statuscode = 'ERROR';
    }
})();

