sys_variable_value.value:
(function execute(inputs, outputs) {
    var attachmentGr = new GlideRecord("sys_attachment");
    if(attachmentGr.get(inputs.attachment_sys_id)){ // Get attachment record
        var base64 = new GlideSysAttachment().getContentBase64(attachmentGr); // Get base64 string of file content
        if(!gs.nil(base64)){ // Check if base64 is returned or falsy value
            outputs.base64 = base64;
            outputs.file_name = attachmentGr.getValue("file_name");
            outputs.file_size_in_bytes = attachmentGr.getValue("size_in_bytes");
            outputs.content_type = attachmentGr.getValue("content_type");
        }else{
            outputs.error = "File size is more than 5 MB";
        }
    }else{
        outputs.error = "File not found.";
    }
})(inputs, outputs);

