sys_variable_value.value:
(function execute(inputs, outputs) {
    var gr = new GlideRecord(inputs.table_name);
    
    // Check if record exists.
    if (!gr.get(inputs.record_sys_id)) {
        outputs.error = "Error: Record with Sys ID '" + inputs.record_sys_id + "' not found in table '" + inputs.table_name + "'.";
        throw new Error('Record not found in table: ' + inputs.tableName);
    }

    // Validate field names
    if (!gr.isValidField(inputs.list_a_field)) {
        outputs.error = "Error: Field '" + inputs.list_a_field + "' is not valid on table '" + inputs.table_name + "'.";
        throw new Error('List A name is not forund: ' + inputs.list_a_field);
    }

    if (!gr.isValidField(inputs.list_b_field)) {
        outputs.error = "Error: Field '" + inputs.list_b_field + "' is not valid on table '" + inputs.table_name + "'.";
        throw new Error('List B name is not forund: ' + inputs.list_b_field);
    }

    function parseGlideList(glideListStr) {
        return glideListStr.split(',').map(function(item) {
            return item.trim();
        }).filter(Boolean);
    }

    var listA = parseGlideList(gr.getValue(inputs.list_a_field));
    var listB = parseGlideList(gr.getValue(inputs.list_b_field));

    var onlyInA = listA.filter(function(item) {
        return listB.indexOf(item) === -1;
    });

    var onlyInB = listB.filter(function(item) {
        return listA.indexOf(item) === -1;
    });

    var common = listA.filter(function(item) {
        return listB.indexOf(item) !== -1;
    });

    outputs.only_in_a = onlyInA.join(',');
    outputs.only_in_b = onlyInB.join(',');
    outputs.common_items = common.join(',');
})(inputs, outputs);

