sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var grtbl = new GlideRecord(inputs.tableName);
if(grtbl.get(inputs.uniqueValue)){
var listValue = grtbl.getValue(inputs.fieldName);
if(listValue){
var ids = listValue.split(',');
var displayValues = [];
ids.forEach(function(id){
var user = new GlideRecord('sys_user');
if(user.get(id)){
displayValues.push(user.getDisplayValue());
}
});
outputs.commaseperatedvalues = displayValues.join(',');
}
else{
outputs.commaseperatedvalues ='';
}
}
else{
outputs.commaseperatedvalues = "Record not found";

}
})(inputs, outputs);

