sys_variable_value.value:
(function execute(inputs, outputs) {
    var arr = [];
    arr = inputs.array_input.split(',').map(function (item) { return item.trim(); });
    var result;

    switch (inputs.operation) {
        case 'join':
            result = arr.join(inputs.delimiter);
            break;

        case 'unique':
            result = [...new Set(arr)];
            break;

        case 'asc':
            result = arr.sort();
            break;

        case 'desc':
            result = arr.sort().reverse();
            break;

        case 'reverse':
            result = arr.reverse();
            break;

        case 'contains':
            result = arr.includes(inputs.value_to_check);
            break;

        case 'length':
            result = arr.length;
            break;

        case 'filterEmpty':
            result = arr.filter(function(x) { return x != null && x !== ''; });
            break;

        default:
            result = arr;
    }

    outputs.result = typeof result === 'object' ? result.join(",") : result;

})(inputs, outputs);

