sys_variable_value.value:
(function execute(inputs, outputs) {
    
    outputs.success = false;
    outputs.extracted_value = inputs.defaultValue || ''; 
    outputs.error_message = '';

    try {
        if (inputs.format === 'json') {
            
            // JSON parsing using global.JSON.parse
            var data = global.JSON.parse(inputs.rawData); 
            
            var pathSegments = inputs.path.split('.');
            var currentValue = data;

            // Traverse the JSON object/array
            for (var i = 0; i < pathSegments.length; i++) {
                var segment = pathSegments[i];

                var arrayMatch = segment.match(/(\w+)\[(\d+)\]/);
                if (arrayMatch) {
                    var key = arrayMatch[1];
                    var index = parseInt(arrayMatch[2]);
                    
                    if (currentValue && currentValue.hasOwnProperty(key) && Array.isArray(currentValue[key]) && currentValue[key].length > index) {
                        currentValue = currentValue[key][index];
                    } else {
                        currentValue = undefined; 
                        break;
                    }
                } 
                else {
                     if (currentValue && currentValue.hasOwnProperty(segment)) {
                        currentValue = currentValue[segment];
                    } else {
                        currentValue = undefined; 
                        break;
                    }
                }
            }

            // Assign the final result (includes the default value fix)
            if (currentValue !== undefined && currentValue !== null) {
                outputs.extracted_value = String(currentValue); 
                outputs.success = true;
            } else {
                outputs.extracted_value = inputs.defaultValue || ''; 
                outputs.error_message = 'JSON path "' + inputs.path + '" not found. Returning default value.';
                outputs.success = false;
            }
            
        } else if (inputs.format === 'xml') {
            
            // XML logic remains the same (requires correct XPath like /root/tag)
            var xmlDoc = new XMLDocument2();
            xmlDoc.parseXML(inputs.rawData);

            var nodeList = xmlDoc.getNodeText(inputs.path);
            
            if (nodeList) {
                outputs.extracted_value = nodeList; 
                outputs.success = true;
            } else {
                // If path is not found, return default value
                outputs.extracted_value = inputs.defaultValue || ''; 
                outputs.error_message = 'XML XPath "' + inputs.path + '" not found. Returning default value.';
                outputs.success = false;
            }
            
        } else {
            outputs.error_message = 'Invalid data format specified. Must be json or xml.';
        }

    } catch (e) {
        var errorMessage = e.toString();
        outputs.error_message = 'FATAL PARSING ERROR (' + inputs.format + '): ' + errorMessage;
    }

})(inputs, outputs);

