sys_variable_value.value:
(function execute(inputs, outputs) {
 try {
    var table = inputs.tableName;
    var recId = inputs.recordSysId;
    var selectedFields = inputs.fields || []; // optional, can be empty

    // Initialize outputs
    outputs.issuccess = false;
    outputs.errorMsg = '';

    if (!table || !recId) {
      var errMsg = 'Missing required inputs: tableName or recordSysId';
      gs.warn(errMsg);
      outputs.errorMsg = errMsg;
      outputs.jsonstring = JSON.stringify({ error: errMsg });
      return;
    }

    var gr = new GlideRecord(table);
    if (!gr.get(recId)) {
      var errMsg = 'Record not found or invalid sys_id: ' + recId;
      gs.warn(errMsg);
      outputs.errorMsg = errMsg;
      outputs.jsonstring = JSON.stringify({ error: errMsg });
      return;
    }

    var obj = {};
    var excludedFields = ['work_notes', 'comments'];

    if (selectedFields.length === 0) {
      var errMsg = 'No fields specified for conversion';
      gs.warn(errMsg);
      outputs.errorMsg = errMsg;
      outputs.jsonstring = JSON.stringify({ error: errMsg });
      return;
    }

    selectedFields.forEach(function(field) {
      if (!gr.isValidField(field)) return;
      if (excludedFields.indexOf(field) !== -1) return;

      var value = gr.getValue(field);
      var fieldType = gr.getElement(field).getED().getInternalType();

      if (fieldType === 'reference') {
        value = gr.getDisplayValue(field);
      }

      if (fieldType === 'choice') {
        value = gr.getDisplayValue(field);
      }

      if (fieldType === 'glide_date' || fieldType === 'glide_date_time' || fieldType === 'glide_time') {
        value = value ? new GlideDateTime(value).getDisplayValueInternal() : null;
      }

      obj[field] = value !== undefined ? value : null;
    });

    outputs.jsonstring = JSON.stringify(obj, null, 2);
    outputs.issuccess = true;  // Mark success here

    gs.info('Generated JSON for ' + table + ':' + recId + '\n' + outputs.jsonstring);

  } catch (ex) {
    var errmsg = 'Server error: ' + ex.message;
    gs.error(errmsg);
    outputs.errorMsg = errmsg;
    outputs.jsonstring = JSON.stringify({ error: errmsg });
    outputs.issuccess = false;
  }
})(inputs, outputs);

