sys_variable_value.value:
(function execute(inputs, outputs) {
var table = inputs.tableName;
    var recId = inputs.recordSysId;
    var selectedFields = inputs.fields || []; // optional, can be empty

    if (!table || !recId) {
        gs.warn('Missing required inputs: tableName or recordSysId');
        outputs.jsonstring = '{}';
        return;
    }

    // Load the record
    var gr = new GlideRecord(table);
    if (!gr.get(recId)) {
        gs.warn('Record not found: ' + table + ' / ' + recId);
        outputs.jsonstring = '{}';
        return;
    }

    var obj = {};

    // Determine fields to process
    var fieldsToProcess = selectedFields.length > 0 ? selectedFields : gr.getFields();

    fieldsToProcess.forEach(function(field) {
        if (!gr.isValidField(field)) return; // skip invalid fields

        var value = gr.getValue(field);
        var fieldType = gr.getElement(field).getED().getInternalType();

        // Handle reference fields: use display value
        if (fieldType === 'reference') {
            var refGR = gr.getDisplayValue(field);
            value = refGR;
        }

        // Handle choice fields: use display value
        if (fieldType === 'choice') {
            value = gr.getDisplayValue(field);
        }

        // Handle date/time fields: convert to ISO string
        if (fieldType === 'glide_date' || fieldType === 'glide_date_time' || fieldType === 'glide_time') {
            value = gr.getValue(field) ? new GlideDateTime(gr.getValue(field)).getDisplayValueInternal() : null;
        }

        obj[field] = value !== undefined ? value : null;
    });

    // Convert to pretty JSON string
    var jsonStr = JSON.stringify(obj, null, 2);

    outputs.jsonstring = jsonStr;

    gs.info('Generated JSON for ' + table + ':' + recId + '\n' + jsonStr);

})(inputs, outputs);

