sys_variable_value.value:
(function execute(inputs, outputs) {
// 1. Check if the tag already exists
    var tagGR = new GlideRecord('label');
    // Used an exact match query for better performance. 
    tagGR.addQuery('name', inputs.tagName);
    tagGR.query();
    if (tagGR.next()) {
        // Tag exists & Inactive: Use the existing, mark as active =true and use the existing Sys ID for output
        if(!tagGR.active){
			tagGR.setValue('active',true);
			tagGR.setValue('viewable_by', 'everyone'); 
			tagGR.update();
			outputs.tag_sys_id = tagGR.sys_id.toString()		
		}else{
		// Tag exists: Use the existing Sys ID for output
        outputs.tag_sys_id = tagGR.sys_id.toString();
		}
    } else {
        // Tag does not exist: Create a new tag
        tagGR.initialize();
        tagGR.setValue('name', inputs.tagName);
        
        // For a shared/public tag, ensure 'viewable_by' is 'everyone'.
        tagGR.setValue('viewable_by', 'everyone'); 
        
        outputs.tag_sys_id = tagGR.insert().toString();
    }
})(inputs, outputs);

