sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var record = new GlideRecord(inputs.tableName);

        if (record.get(inputs.sysId)) {
            record.work_notes = inputs.workNotesText;
            record.update();

            outputs.success = true;
            outputs.code = "200"; // Success
            outputs.message = "Work notes added successfully.";
        } else {
            outputs.success = false;
            outputs.code = "404"; // Not Found
            outputs.message = "Record not found.";
        }
    } catch (e) {
        outputs.success = false;
        outputs.code = "500"; // Internal Server Error
        outputs.message = "Error: " + e.message;
    }
})(inputs, outputs);

