sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var arr=[];
var str;
var k= inputs.parameter;
var gr= new GlideRecord("task");
gr.addActiveQuery();
gr.addQuery("assigned_to",k);
gr.query();
while(gr.next()){
arr.push(gr.number);
}
if(arr.length>0){
    str="Please find the active ticket : "+arr.join(",");
}
else{
    str="No active tickets";
}
outputs.strng=str;
})(inputs, outputs);

