sys_variable_value.value:
(function execute(inputs, outputs) {
 var sysid = sn_discovery.DiscoveryAPI.discoverIpAddress(inputs.ipAddress);

var isExecuted = 'false';
 var discoveryStatusGr = new GlideRecord('discovery_status');
    if(discoveryStatusGr.get(sysid)){
        isExecuted = 'true';
    }
    outputs.disco_status_record = discoveryStatusGr;
    outputs.executed = isExecuted;
    outputs.disco_sysid = sysid.toString();
})(inputs, outputs);

