sys_variable_value.value:
(function execute(inputs, outputs) {

var recordGr  = new GlideRecord('discovery_status');

if(recordGr.get(inputs.sysId)){
    outputs.discovery_execution_status = recordGr.getDisplayValue('state')
}

})(inputs, outputs);

