sys_variable_value.value:
(function execute(inputs, outputs) {
    var attachmentGr = new GlideRecord("sys_attachment");
    if(attachmentGr.get(inputs.attachment_sys_id)){
        var attachment = new GlideSysAttachment();
        var base64 = attachment.getContentBase64(attachmentGr);
        if(!gs.nil(base64)){
            outputs.file_name = attachmentGr.getValue('file_name');
            outputs.content_type = attachmentGr.getValue('content_type');
            outputs.attachment_size_in_bytes = attachmentGr.getValue('size_bytes');
            outputs.base64 = base64;
        }else{
            outputs.error = "File size is more than 5 MB";
        }
    }else{
        outputs.error = "No record found";
    }
})(inputs, outputs);

