sys_variable_value.value:
(function execute(inputs, outputs) {

    var toClean = inputs.rawHTML;
    //Clean HTML
    toClean = toClean.replace(/<\/?[^>]+(>|$)/g, "");
    //Output clean string
    outputs.santized_string = toClean;

})(inputs, outputs);

