sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var gdt = new GlideScheduleDateTime(inputs.sourceDateTime);
        var convertedStr = gdt.convertTimeZone(inputs.sourceTimeZone, inputs.targetTimeZone);
        var convertedGDT = new GlideDateTime(convertedStr);
        
        outputs.converteddatetime = convertedGDT.getDisplayValue();
        outputs.success = true;
        outputs.message = "DateTime converted successfully.";
    } catch (e) {
        outputs.success = false;
        outputs.message = "Error converting DateTime: " + e.message;
    }
})(inputs, outputs);

