sys_variable_value.value:
(function execute(inputs, outputs) {

    var startDate = new GlideDateTime(inputs.start_date);
    var endDate = new GlideDateTime(inputs.end_date);
    var schedule = new GlideSchedule(inputs.schedule_sysid);
    
    if (startDate.getNumericValue() > endDate.getNumericValue()) {
        outputs.business_duration_display = "Error: End date can't be before start date";
        throw new Error("Date_Validation_failed");
    }

    try {
        var durationMs = schedule.duration(startDate, endDate); //business duration in milliseconds

        var glideDuration = new GlideDuration(durationMs);   //creating glideduration object

        //setting output of the script step
        outputs.business_duration_display = glideDuration.getDisplayValue();
    }
    catch (e) {
        outputs.business_duration_display = "Error: Calculation failed due to invalid schedule or system error. Details: " + e.getMessage();
    }

})(inputs, outputs);

