sys_variable_value.value:
(function execute(inputs, outputs) {
    // Validate Input Early (Fail-Fast)
    if (!inputs.groupId) {
        gs.warn("⚠️ Action aborted: No Assignment Group provided.");

        outputs.action_status = "error";
        outputs.error_message = "No Assignment Group provided. Please ensure 'groupId' input is mapped.";
        outputs.error_type = "validation";
        return;
    }

    try {
        
        var groupId = inputs.groupId;
        var result = [];
        var count = 0;

        
        var gr = new GlideRecord('incident');
        gr.addQuery('assignment_group', groupId);
        gr.addQuery('active', true);
        gr.addQuery('state', '!=', 6); // exclude Resolved
        gr.addQuery('state', '!=', 7); // exclude Closed
        gr.query();

        while (gr.next()) {
            result.push({
                number: gr.getValue('number'),
                sys_id: gr.getUniqueValue(),
                state: gr.getDisplayValue('state'),
                priority: gr.getDisplayValue('priority')
            });
            count++;
        }

        // Success Case
        outputs.active_incident_count = count;
        outputs.incident_list = JSON.stringify(result);
        outputs.action_status = "success";
        outputs.error_message = "";

        gs.info("✅ Retrieved " + count + " active incidents for group: " + groupId);

    } catch (e) {
        // 🧩 5. Handle unexpected runtime errors
        gs.error("❌ Error fetching active incidents: " + e);

        outputs.active_incident_count = 0;
        outputs.incident_list = "[]";
        outputs.action_status = "error";
        outputs.error_type = "runtime";
        outputs.error_message = e.toString();
    }

})(inputs, outputs);

