sys_variable_value.value:
(function execute(inputs, outputs) {
  var groupId  = inputs.group;
  var limit    = inputs.limit;
  var sort     = inputs.sort;
  var openOnly = inputs.open_only;
  var gr = new GlideRecord('incident');
  gr.addQuery('assignment_group', groupId);
  if (openOnly) gr.addQuery('active', true);

  if (sort === 'recent') {
    gr.orderByDesc('opened_at');
  } else if (sort === 'updated') {
    gr.orderByDesc('sys_updated_on');
  } else {
    gr.orderBy('priority');      // 1 → 5
    gr.orderByDesc('opened_at'); // tie-breaker
  }

  gr.setLimit(limit);
  gr.query();

  var rows = [];
  while (gr.next()) {
    rows.push({
      sys_id: gr.getUniqueValue(),
      number: gr.getValue('number'),
      short_description: gr.getValue('short_description') || '',
      priority: parseInt(gr.getValue('priority'), 10) || 0,
      priority_display: gr.getDisplayValue('priority'),
      state: parseInt(gr.getValue('state'), 10) || 0,
      state_display: gr.getDisplayValue('state'),
      opened_at: gr.getValue('opened_at'),
      sys_updated_on: gr.getValue('sys_updated_on'),
      caller_id: gr.getValue('caller_id'),
      caller_display: gr.getDisplayValue('caller_id'),
      assignment_group: gr.getValue('assignment_group'),
      assignment_group_display: gr.getDisplayValue('assignment_group')
    });
  }

  outputs.incidents_json = JSON.stringify(rows);
  outputs.count = rows.length;
})(inputs, outputs);

