sys_variable_value.value:
(function execute(inputs, outputs) {
    
    // Initialize outputs
    outputs.status = 'Closed';
    outputs.open_task_count = 0;

    // Get the RITM record
    var ritmGR = new GlideRecord('sc_req_item');
    if (!ritmGR.get(inputs.ritm_sys_id)) {
        outputs.status = 'Invalid RITM';
        return;
    }

    // Query related Catalog Tasks
    var taskGR = new GlideRecord('sc_task');
    taskGR.addQuery('request_item', inputs.ritm_sys_id);
    taskGR.query();

    var openCount = 0;

    while (taskGR.next()) {
        if (taskGR.active) {
            openCount++;
        }
    }

    outputs.open_task_count = openCount;
    outputs.status = openCount > 0 ? 'Open' : 'Closed';

})(inputs, outputs);

