sys_variable_value.value:
(function execute(inputs, outputs) {
    // Map inputs to variables
    var table = inputs.u_table_name;
    var sysId = inputs.u_record_sys_id;
    var parentField = inputs.u_parent_field || 'parent';
    var maxDepth = inputs.u_max_depth || 10;
    var direction = inputs.u_direction;
    var startName = ''; 
    var initialParentSysId = '';

    // Initialize data structures
    var hierarchy = [];
    var visitedSysIds = {}; 
    var allPaths = [];      

    // --- Validation Start ---
    try {
        
        // V1. Validate Parent Field Existence on the Table
        var dictGr = new GlideRecord('sys_dictionary');
        dictGr.addQuery('name', table);
        dictGr.addQuery('element', parentField);
        dictGr.query();
        if (!dictGr.next()) {
            throw new Error("Parent field '" + parentField + "' does not exist on table '" + table + "'.");
        }

        // V2. Validate Record Existence (Sys ID)
        var startGr = new GlideRecord(table);
        if (!startGr.get(sysId)) {
            throw new Error("Record with sys_id '" + sysId + "' not found in table '" + table + "'.");
        }
        
        // Populate starting details if validation passes
        startName = startGr.getDisplayValue();
        initialParentSysId = startGr.getValue(parentField);

    } catch (e) {
        // If any validation fails, set error outputs and exit the script execution
        outputs.u_hierarchy_array = '[]'; 
        outputs.u_breadcrumb_string = 'VALIDATION FAILED: ' + e.message;
        outputs.u_success = false;
        return; // Terminate execution early
    }
    // --- Validation End ---

    
    // --- Recursive Function for Ancestors (Parents) ---
    function getAncestors(currentSysId, currentDepth) {
        if (currentDepth >= maxDepth) return;
        
        if (visitedSysIds.hasOwnProperty(currentSysId)) return; 
        visitedSysIds[currentSysId] = true; 

        var gr = new GlideRecord(table);
        if (gr.get(currentSysId)) {
            var parentSysId = gr.getValue(parentField);
            var currentName = gr.getDisplayValue();
            
            hierarchy.push({
                sys_id: currentSysId,
                name: currentName,
                depth: currentDepth,
                path: '' 
            });

            if (parentSysId) {
                getAncestors(parentSysId, currentDepth + 1);
            }
        }
    }


    // --- Recursive Function for Descendants (Children) ---
    function getDescendants(currentSysId, currentPathArray, currentDepth) {
        if (currentDepth >= maxDepth) return;
        
        if (visitedSysIds.hasOwnProperty(currentSysId)) return;
        visitedSysIds[currentSysId] = true;

        var gr = new GlideRecord(table);
        gr.addQuery(parentField, currentSysId);
        gr.query();

        while (gr.next()) {
            var childSysId = gr.sys_id.toString();
            var childName = gr.getDisplayValue();
            
            var childPathArray = currentPathArray.slice(); 
            childPathArray.push(childName);
            
            hierarchy.push({
                sys_id: childSysId,
                name: childName,
                depth: currentDepth + 1,
                path: childPathArray.join(' -> ')
            });

            allPaths.push(childPathArray.join(' -> '));

            getDescendants(childSysId, childPathArray, currentDepth + 1);
        }
    }


    // --- Main Execution Logic ---
    try {
        if (direction === 'Ancestors') {
            if (initialParentSysId) {
                getAncestors(initialParentSysId, 0); 
            }
            
            // Post-processing for Ancestors: Add starting node, calculate path, renumber depth.
            hierarchy.unshift({
                sys_id: sysId,
                name: startName,
                depth: hierarchy.length, 
                path: ''
            });

            var tempPathNames = [];
            for (var i = hierarchy.length - 1; i >= 0; i--) {
                tempPathNames.push(hierarchy[i].name);
                hierarchy[i].path = tempPathNames.join(' -> ');
                hierarchy[i].depth = (hierarchy.length - 1) - i; 
            }
            
            // The full path is the last item built (index 0 after final ordering)
            if (hierarchy.length > 0) {
               allPaths.push(hierarchy[0].path);
            }

        } else if (direction === 'Descendants') {
            getDescendants(sysId, [startName], 0); 
        }

        // Set Outputs
        outputs.u_hierarchy_array = JSON.stringify(hierarchy); 
        outputs.u_breadcrumb_string = allPaths.join('\n'); 
        outputs.u_success = true;

    } catch (e) {
        // Fallback for unexpected runtime error during recursion/post-processing
        gs.error('Runtime error in Get Hierarchy Action: ' + e);
        outputs.u_hierarchy_array = '[]'; 
        outputs.u_breadcrumb_string = 'RUNTIME ERROR: ' + e.message;
        outputs.u_success = false;
    }

})(inputs, outputs);

