sys_variable_value.value:
(function execute(inputs, outputs) {
  try {
    var taskSysId = inputs.task.getUniqueValue();
    var taskTable = inputs.task.getValue('sys_class_name');
    var owner = inputs.owner || gs.getUserID();

    var grList = new GlideRecord('checklist');
    grList.addQuery('document', taskSysId);
    grList.addQuery('table', taskTable);
    grList.query();

    if (grList.next())
      throw new Error(gs.getMessage("This task already contains a checklist"));
    grList.setValue('document', taskSysId);
    grList.setValue('name', inputs.name);
    grList.setValue('owner', owner);
    grList.setValue('table', taskTable);
    var checklistId = grList.insert();

    var itemArray = inputs.items.split('\n');
    var checklistCount = 0;

    for (var i = 0; i < itemArray.length; i++) {
      var itemName = itemArray[i].trim();
      if (itemName.length === 0) {
        continue; // Skip blank or whitespace-only lines
      }

      var grItem = new GlideRecord('checklist_item');
      grItem.setValue('checklist', checklistId);
      grItem.setValue('complete', false);
      grItem.setValue('name', itemName);
      grItem.setValue('order', checklistCount);
      grItem.insert();

      checklistCount++; // Only increment for valid items
    }

    outputs.checklist_count = checklistCount;
    outputs.success = true;
  } catch (e) {
    outputs.error = e.message;
    outputs.checklist_count = 0;
    outputs.success = false;
  }
})(inputs, outputs);

