sys_variable_value.value:
(function execute(inputs, outputs) {
/*
reg : regex defined by user
txtval : text to be scanned and replaced
*/

var pattern = new RegExp(inputs.reg , 'jgim'); // i,m,g are regex identifiers.

outputs.replaced_text = inputs.txtval.replaceAll(pattern , inputs.replacingStr);

})(inputs, outputs);

