sys_variable_value.value:
(function execute(inputs, outputs) {
// add Child (holiday schedule)
while(inputs.scheduleList.next()) {
    var chSch = new GlideRecord('cmn_other_schedule');
    chSch.initialize();
    chSch.schedule = inputs.scheduleList.sys_id;
    chSch.child_schedule = inputs.holidaySch.sys_id;
    chSch.insert();
}
  
})(inputs, outputs);

