sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var str = inputs.inputString || "";
        var op = (inputs.stringOperation || "").toLowerCase();

        switch (op) {
            case "uppercase":
                outputs.result = str.toUpperCase();
                break;

            case "lowercase":
                outputs.result = str.toLowerCase();
                break;

            case "titlecase":
                outputs.result = str.replace(/\w\S*/g, function(txt) {
                    return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
                });
                break;

            case "sentencecase":
                outputs.result = str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
                break;

            case "trim":
                outputs.result = str.trim();
                break;

            default:
                outputs.result = str;
        }

        outputs.success = true;
        outputs.message = "Operation executed successfully.";

    } catch (e) {
        outputs.success = false;
        outputs.message = "Error: " + e.message;
    }
})(inputs, outputs);

