sys_variable_value.value:
(function execute(inputs, outputs) {
  function toArray(val) {

    var isArray = Array.isArray || function (v) { return Object.prototype.toString.call(v) === '[object Array]'; };
    if (isArray(val)) return val;

    if (typeof val === 'string') {
      var s = (val + '').trim();
      if (!s) return [];
      try {
        var parsed = JSON.parse(s);
        return isArray(parsed) ? parsed : [];
      } catch (e) { return []; }
    }
    return [];
  }

  function getByPath(obj, path) {
    if (!obj || !path) return undefined;
    var parts = (path + '').split('.');
    var cur = obj;
    for (var i = 0; i < parts.length; i++) {
      if (cur == null) return undefined;
      cur = cur[parts[i]];
    }
    return cur;
  }

  function norm(val, opts) {
    if (val === null || val === undefined) return '';
    var v = String(val);
    if (opts.trim) v = v.trim();
    if (opts.ci) v = v.toLowerCase();
    return v;
  }

  try {
    var arr = toArray(inputs.array);
    var key = (inputs.key + '').trim();
    var keep = (inputs.keep || 'first') === 'last' ? 'last' : 'first';
    var ci = inputs.case_insensitive === true || (inputs.case_insensitive + '') === 'true';
    var tr = inputs.trim === true || (inputs.trim + '') === 'true';

    if (!key) { outputs.result = { ok:false, code:'INVALID_INPUT', message:'key is required' }; return; }

    var countIn = arr.length;
    if (countIn === 0) {
      outputs.result = { ok:true, count_in:0, count_out:0, removed:0, items:[] }; return;
    }

    var seen = Object.create(null), out = [], opts = { ci:ci, trim:tr };

    if (keep === 'first') {
      for (var i=0;i<arr.length;i++){
        var it = arr[i], k = norm(getByPath(it, key), opts);
        if (seen[k]) continue;
        seen[k] = 1; out.push(it);
      }
    } else {
      for (var j=arr.length-1;j>=0;j--){
        var it2 = arr[j], k2 = norm(getByPath(it2, key), opts);
        if (seen[k2]) continue;
        seen[k2] = 1; out.push(it2);
      }
      out.reverse();
    }

    outputs.result = {
      ok:true,
      count_in: countIn,
      count_out: out.length,
      removed: countIn - out.length,
      items: out
    };
  } catch (e) {
    outputs.result = { ok:false, code:'UNEXPECTED_ERROR', message:(e && e.message) || 'unexpected' };
  }
  
})(inputs, outputs);

