sys_variable_value.value:
(function execute(inputs, outputs) {

var currentUser = gs.getUserID();
gs.getSession().impersonate(inputs.user);

try {
    var recordGR = new GlideRecord(inputs.table_name);
    recordGR.get(inputs.record);

    switch(inputs.access_type){
        case  'read':
        outputs.can_perform_operation = recordGR.canRead();
        break;

        case  'write':
        outputs.can_perform_operation = recordGR.canWrite();
        break;

        case  'create':
        outputs.can_perform_operation = recordGR.canCreate();
        break;

        case  'delete':
        outputs.can_perform_operation = recordGR.canDelete();
        break;

        default:
        throw new Error('Invalid Operation');
    }

    gs.getSession().impersonate(currentUser);

} catch (e){
    gs.getSession().impersonate(currentUser);
    throw e;
}

})(inputs, outputs);

