sys_variable_value.value:
(function execute(inputs, outputs) {
    var d;
    var strt= new GlideDateTime(inputs.strt_date);//strt date
    var nd= new GlideDateTime(inputs.nd_date); //end date
    var n=nd.getNumericValue(); //numeric value of end date
    var st=strt.getNumericValue();//numeric value of start date
    if(n<st){
        var message="End date is less than start date, please verify once again.";
        gs.error(message);
        throw(message);
    }
    else if(n==st){
        d=0
    } else{
        var diff= n-st; //difference in milliseconds
        d= diff/(1000*60*60*24); //obtained difference conversion to days
    }
    
    outputs.num=d;
    
// ... code ...
})(inputs, outputs);

