sys_variable_value.value:
(function execute(inputs, outputs) {
    var keywordMap = {
        "vpn": "Network",
        "outlook": "Email",
        "printer": "Hardware",
        "wifi": "Connectivity",
        "slow": "Performance",
        "error": "Issue",
        "login": "Access",
        "password": "Security"
    };

    var foundTags = [];

    var desc = inputs.description.toLowerCase();

    for (var keyword in keywordMap) {
        if (desc.includes(keyword)) {
            foundTags.push(keywordMap[keyword]);
        }
    }

    outputs.tags = foundTags;
})(inputs, outputs);

