sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var qrText = inputs.text_to_encode;
        var format = inputs.image_format || 'png';

        var qrService = new sn_ws.RESTMessageV2();
        qrService.setEndpoint('https://api.qrserver.com/v1/create-qr-code/?data=' + encodeURIComponent(qrText) + '&format=' + format);
        qrService.setHttpMethod('GET');

        var response = qrService.execute();
        var statusCode = response.getStatusCode();

        if (statusCode === 200) {
            outputs.qr_code_url = qrService.getEndpoint(); // or use response.getBody() if image is returned inline.
        } else {
            throw new Error('QR code generation failed with status code: ' + statusCode);
        }
    } catch (error) {
        outputs.qr_code_url = 'Error: ' + error.message;
        throw new Error('QR Code Generation Error: ' + error.message);
    }
})(inputs, outputs);

